import{_ as d,c as a,o as e,aj as r}from"./chunks/framework.DbINzAG1.js";const _=JSON.parse('{"title":"接口列表","description":"","frontmatter":{},"headers":[],"relativePath":"plus/gateway/interface/overview/接口清单.md","filePath":"plus/gateway/interface/overview/接口清单.md","lastUpdated":1744547023000}'),i={name:"plus/gateway/interface/overview/接口清单.md"};function h(n,t,l,s,o,c){return e(),a("div",null,t[0]||(t[0]=[r('<h1 id="接口列表" tabindex="-1">接口列表 <a class="header-anchor" href="#接口列表">¶</a></h1><div class="tip custom-block"><p class="custom-block-title">提示</p><p>业务系统想接入支付网关的话，不需要集成到业务系统里，只需要单独部署一份支付系统，然后业务系统通过接口调用即可拥有对应的支付能力， 不会对原业务系统的架构产生影响。如果是Java项目，可以使用<a href="./SDK使用说明.html">支付网关SDK</a>简化接入流程， 其他语言可以参照接口文档中的说明使用HTTP接口方式接入。</p></div><h2 id="接入流程概括" tabindex="-1">接入流程概括 <a class="header-anchor" href="#接入流程概括">¶</a></h2><ol><li>部署并启动支付系统，并登录到支付网关后台管理端</li><li>创建应用并配置支付通道、接口配置、签名方式、密钥等参数</li><li>在业务系统中配置支付网关的调用地址，启用签名验证的话，需要将密钥配置到业务系统中</li><li>业务系统使用支付网关提供的接口进行调用支付的能力</li></ol><h2 id="交易接口列表" tabindex="-1">交易接口列表 <a class="header-anchor" href="#交易接口列表">¶</a></h2><table tabindex="0"><thead><tr><th>名称</th><th>接口地址</th><th>支持回调</th><th>备注</th></tr></thead><tbody><tr><td>统一支付接口</td><td>/unipay/pay</td><td>支持</td><td></td></tr><tr><td>网关支付接口</td><td>/unipay/checkout/create</td><td>支持</td><td></td></tr><tr><td>统一退款接口</td><td>/unipay/refund</td><td>支持</td><td></td></tr><tr><td>统一转账接口</td><td>/unipay/transfer</td><td>支持</td><td></td></tr><tr><td>支付关闭和撤销接口</td><td>/unipay/close</td><td>支持</td><td>不支持单独传输通知地址，会直接将通知发往支付时所传输的地址，同时支持以撤销的方式进行关闭</td></tr></tbody></table><h2 id="查询接口列表" tabindex="-1">查询接口列表 <a class="header-anchor" href="#查询接口列表">¶</a></h2><table tabindex="0"><thead><tr><th>名称</th><th>接口地址</th><th>备注</th></tr></thead><tbody><tr><td>支付订单查询接口</td><td>/unipay/query/payOrder</td><td></td></tr><tr><td>退款订单查询接口</td><td>/unipay/query/refundOrder</td><td></td></tr><tr><td>转账订单查询接口</td><td>/unipay/query/transferOrder</td><td></td></tr></tbody></table>',8)]))}const u=d(i,[["render",h]]);export{_ as __pageData,u as default};
