import{_ as a,c as d,o as e,aj as r}from"./chunks/framework.DbINzAG1.js";const m=JSON.parse('{"title":"分账配置","description":"","frontmatter":{},"headers":[],"relativePath":"plus/manual/admin/config/分账配置.md","filePath":"plus/manual/admin/config/分账配置.md","lastUpdated":1746454016000}'),h={name:"plus/manual/admin/config/分账配置.md"};function c(i,t,s,o,n,l){return e(),d("div",null,t[0]||(t[0]=[r('<h1 id="分账配置" tabindex="-1">分账配置 <a class="header-anchor" href="#分账配置">¶</a></h1><h2 id="功能介绍" tabindex="-1">功能介绍 <a class="header-anchor" href="#功能介绍">¶</a></h2><div class="tip custom-block"><p class="custom-block-title">说明</p><p>此处分账配置只进行信息的配置，分账能力的开通和分账接收方的注册需要自行进行处理，可以在系统中的支付通道菜单下进行处理，也可以在相应的通道官方平台上进行操作。</p></div><h3 id="分账配置-1" tabindex="-1">分账配置 <a class="header-anchor" href="#分账配置-1">¶</a></h3><h4 id="配置说明" tabindex="-1">配置说明 <a class="header-anchor" href="#配置说明">¶</a></h4><table tabindex="0"><thead><tr><th>参数</th><th>功能</th></tr></thead><tbody><tr><td>自动分账</td><td>开启后，如果创建支付订单时设置为了自动分账，将会对订单自动分账</td></tr><tr><td>自动完结</td><td>开启自动完结后，分账完成后，会自动对分账单进行完结，解冻剩余的资金</td></tr><tr><td>自动分账起始金额</td><td>只有大于或等于其金额的订单才会进行自动分账</td></tr><tr><td>分账延迟时间</td><td>支付订单完成后多久会自动进行分账操作</td></tr></tbody></table><h3 id="分账接收方" tabindex="-1">分账接收方 <a class="header-anchor" href="#分账接收方">¶</a></h3><blockquote><p>创建分账接收方时，需要先选择<code>接收方通道</code>，然后下一步进行具体分账接收方的信息填写，不同的通道的分账接收方信息填写数据不同，具体请参考通道的官方文档。</p></blockquote><h4 id="通用参数说明" tabindex="-1">通用参数说明 <a class="header-anchor" href="#通用参数说明">¶</a></h4><table tabindex="0"><thead><tr><th>参数</th><th>功能</th></tr></thead><tbody><tr><td>名称</td><td>分账接收方的名称</td></tr><tr><td>接收方编号</td><td>创建分账接收方时，自动生成的唯一标识，不可修改</td></tr><tr><td>接收方账号</td><td></td></tr><tr><td>所属通道</td><td></td></tr><tr><td>接收方类型</td><td>接收方账号的类型，主要有商户号、用户ID、OpenId等</td></tr></tbody></table><h3 id="分账组" tabindex="-1">分账组 <a class="header-anchor" href="#分账组">¶</a></h3><blockquote><p>创建分账组可以实现指定分账比例、指定人员的分账配置组合，通过设置默认分账组，可以实现支付 订单的自动分账。</p></blockquote><h4 id="步骤" tabindex="-1">步骤 <a class="header-anchor" href="#步骤">¶</a></h4><ul><li>分账组列表页面点击<code>新建</code>后填写信息创建分账组</li><li>点击 <code>接收方配置</code> -&gt; <code>添加分账接收方</code>，选中要添加的分账接收方加到分账组中</li><li>在<code>接收方配置</code>列表页面中<code>分账比例</code>列设置每个接收方的分账比例</li><li>回到分账组列表页面，点击<code>默认分账组</code>，设置默认分账组，默认分账组设置后，开启分账时，如果没有选择分账组，则会自动选择默认分账组进行分账。</li></ul><h2 id="操作截图" tabindex="-1">操作截图 <a class="header-anchor" href="#操作截图">¶</a></h2><h3 id="分账配置-2" tabindex="-1">分账配置 <a class="header-anchor" href="#分账配置-2">¶</a></h3><p><img src="https://cdn.jsdmirror.com/gh/xxm1995/picx-images-hosting@master/20250505/wechat_2025-05-05_165648_510.5c16vk9vyz.webp" alt="wechat_2025-05-05_165648_510" loading="lazy"></p><h3 id="分账接收方-1" tabindex="-1">分账接收方 <a class="header-anchor" href="#分账接收方-1">¶</a></h3><p><img src="https://cdn.jsdmirror.com/gh/xxm1995/picx-images-hosting@master/20250505/wechat_2025-05-05_170304_313.4n7xbjqjsh.webp" alt="wechat_2025-05-05_170304_313" loading="lazy"><img src="https://cdn.jsdmirror.com/gh/xxm1995/picx-images-hosting@master/20250505/wechat_2025-05-05_165709_400.9dd69ydzbx.webp" alt="wechat_2025-05-05_165709_400" loading="lazy"></p><h3 id="分账组-1" tabindex="-1">分账组 <a class="header-anchor" href="#分账组-1">¶</a></h3><p><img src="https://cdn.jsdmirror.com/gh/xxm1995/picx-images-hosting@master/20250505/wechat_2025-05-05_165753_294.8dx2wsb866.webp" alt="wechat_2025-05-05_165753_294" loading="lazy"><img src="https://cdn.jsdmirror.com/gh/xxm1995/picx-images-hosting@master/20250505/wechat_2025-05-05_165811_555.73u5qgt8v3.webp" alt="wechat_2025-05-05_165811_555" loading="lazy"><img src="https://cdn.jsdmirror.com/gh/xxm1995/picx-images-hosting@master/20250505/wechat_2025-05-05_165911_007.2dows21mhs.webp" alt="wechat_2025-05-05_165911_007" loading="lazy"></p>',21)]))}const p=a(h,[["render",c]]);export{m as __pageData,p as default};
