import{_ as e,c as t,o as i,aj as l}from"./chunks/framework.DbINzAG1.js";const h=JSON.parse('{"title":"商户管理","description":"","frontmatter":{},"headers":[],"relativePath":"plus/manual/admin/config/商户管理.md","filePath":"plus/manual/admin/config/商户管理.md","lastUpdated":1746773202000}'),s={name:"plus/manual/admin/config/商户管理.md"};function c(o,a,n,_,p,r){return i(),t("div",null,a[0]||(a[0]=[l('<h1 id="商户管理" tabindex="-1">商户管理 <a class="header-anchor" href="#商户管理">¶</a></h1><div class="tip custom-block"><p class="custom-block-title">提示</p><p>商户分为两种：普通商户和特约商户。</p><blockquote><p>普通商户: 普通商户在系统中可以自行任意配置支付参数，不需要关联服务商，通常用于公司内部其他系统的对接使用，无法进行商户进件操作。性质偏向支付中台类型。</p></blockquote><blockquote><p>特约商户: 特约商户需要关联服务商，同时可以在运营的协助下进行商户进件，只能使用服务商模式进行支付，，无法自行任意进行支付参数的配置。性质偏向聚合支付平台。</p></blockquote></div><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项">¶</a></h2><ul><li>特约商户注册时必须选择所属的服务商，并且后续无法更改</li><li>创建时会自动生成唯一的商户号，后续操作和关联数据使用的都是商户号，一经生成无法修改</li><li>创建商户后可以创建商户管理员，创建的管理员可以在商户平台进行登陆，根据商户类型分配给对应的角色</li><li>商户管理员用户的管理在系统管理中的用户管理进行管理，而不是在商户列表进行管理</li></ul><h2 id="操作截图" tabindex="-1">操作截图 <a class="header-anchor" href="#操作截图">¶</a></h2><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/picx-images-hosting@master/20250504/wechat_2025-05-04_183715_016.lvxw0944a.webp" alt="wechat_2025-05-04_183715_016" loading="lazy"></p><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/picx-images-hosting@master/20250504/wechat_2025-05-04_183725_673.ma9pento.webp" alt="wechat_2025-05-04_183725_673" loading="lazy"></p>',7)]))}const m=e(s,[["render",c]]);export{h as __pageData,m as default};
