import{_ as d,c as a,o as e,aj as r}from"./chunks/framework.DbINzAG1.js";const _=JSON.parse('{"title":"商户应用配置","description":"","frontmatter":{},"headers":[],"relativePath":"plus/manual/admin/config/应用配置.md","filePath":"plus/manual/admin/config/应用配置.md","lastUpdated":1746773202000}'),c={name:"plus/manual/admin/config/应用配置.md"};function o(s,t,h,i,n,l){return e(),a("div",null,t[0]||(t[0]=[r('<h1 id="商户应用配置" tabindex="-1">商户应用配置 <a class="header-anchor" href="#商户应用配置">¶</a></h1><div class="tip custom-block"><p class="custom-block-title">提示</p><p>商户应用配置功能是整个系统的核心核心，包括支付通道、订阅通知、分账管理、网关支付等功能都在围绕着，此处仅介绍商户应用相关的配置，其他配置功能请见其他章节。</p><blockquote><p>普通商户创建的应用为普通应用，特约商户创建的应用为特约应用，主要在支付通道配置上有差异</p></blockquote></div><h2 id="参数介绍" tabindex="-1">参数介绍 <a class="header-anchor" href="#参数介绍">¶</a></h2><table tabindex="0"><thead><tr><th>参数</th><th>功能</th></tr></thead><tbody><tr><td>应用号</td><td>该应用的唯一标识，进行接口对接时会使用</td></tr><tr><td>应用名称</td><td>应用的名称</td></tr><tr><td>支付限额</td><td>单次支付发起时允许的最大金额，单位为元</td></tr><tr><td>订单超时时间</td><td>订单默认超时时间，单位为分钟，创建支付时不传输超时时间默认使用此配置，注意：不同的通道有各自特殊的规定，会出现与配置的超时时间不一致的情况</td></tr><tr><td>校验请求超时时间</td><td>开启此选项后后，将对接收到的请求时间误差进行校验，用于减少重放攻击</td></tr><tr><td>请求超时时间</td><td>开启校验请求超时时间后，对请求时间误差时间超过该设定值的请求，进行拦截</td></tr><tr><td>请求验签</td><td>开启验签后，将对网关接口接收到的请求进行签名的校验，响应对象无论开不开启验签都会进行签名</td></tr><tr><td>签名方式</td><td>支持HMAC_SHA256、MD5、SM3三种，推荐使用HMAC_SHA256</td></tr><tr><td>签名秘钥</td><td>签名时所使用的密钥值</td></tr><tr><td>通知方式</td><td>如果订阅消息通知的话,会使用选择的通知方式进行通知，目前只有HTTP可用</td></tr><tr><td>通知地址</td><td>通知地址，当通知方式为HTTP时，会向该地址发送通知，通知格式为JSON</td></tr></tbody></table><h2 id="操作介绍" tabindex="-1">操作介绍 <a class="header-anchor" href="#操作介绍">¶</a></h2><table tabindex="0"><thead><tr><th>操作</th><th>功能</th></tr></thead><tbody><tr><td>通道配置</td><td>对各种支付通道进行配置，详情见通道配置章节</td></tr><tr><td>订阅配置</td><td>选择订阅哪些内容的消息类型，配合通知方式和通知地址一起使用</td></tr><tr><td>网关支付</td><td>对网关支付内容进行配置，详情见网关支付章节</td></tr><tr><td>收款终端</td><td>管理和配置收款终端，详情见收银码牌配置章节</td></tr><tr><td>分账配置</td><td>对当前应用的分账模式、分账接收方、分账模式进行配置，详情见分账配置章节</td></tr></tbody></table><h2 id="订阅配置" tabindex="-1">订阅配置 <a class="header-anchor" href="#订阅配置">¶</a></h2><blockquote><p>通过选取要订阅的内容，配合配置中的<code>通知方式</code>和<code>通知地址</code>配置项，可以实现将订阅的内容通知到业务系统，只有<code>通知方式</code>为<code>http</code>时，通知订阅才会生效。</p></blockquote><h2 id="操作截图" tabindex="-1">操作截图 <a class="header-anchor" href="#操作截图">¶</a></h2><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/picx-images-hosting@master/20250504/wechat_2025-05-04_224016_192.491hjl44yp.webp" alt="wechat_2025-05-04_224016_192" loading="lazy"></p><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/picx-images-hosting@master/20250504/wechat_2025-05-04_224002_732.7pi5701lr.webp" alt="wechat_2025-05-04_224002_732" loading="lazy"></p><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/picx-images-hosting@master/daxpay/%E5%BE%AE%E4%BF%A1%E6%88%AA%E5%9B%BE_20241208111000.1027s72u5v.webp" alt="微信截图_20241208111000" loading="lazy"></p>',12)]))}const m=d(c,[["render",o]]);export{_ as __pageData,m as default};
