import{_ as e,c as t,o as i,aj as s}from"./chunks/framework.DbINzAG1.js";const p=JSON.parse('{"title":"收款终端管理","description":"","frontmatter":{},"headers":[],"relativePath":"plus/manual/admin/config/收款终端.md","filePath":"plus/manual/admin/config/收款终端.md","lastUpdated":1746773202000}'),c={name:"plus/manual/admin/config/收款终端.md"};function l(n,a,r,o,_,d){return i(),t("div",null,a[0]||(a[0]=[s('<h1 id="收款终端管理" tabindex="-1">收款终端管理 <a class="header-anchor" href="#收款终端管理">¶</a></h1><div class="tip custom-block"><p class="custom-block-title">提示</p><p>根据国家259号文要求，在POS和被扫等模式下，商户必须提供收款终端设备信息，以方便支付平台进行设备识别。所以部分通道需要对收款终端设备进行提前报送处理， 部分的需要在支付时上送所用的设备，所有需要上报的终端设备信息，都可以在此进行管理和上报。</p></div><h2 id="功能介绍" tabindex="-1">功能介绍 <a class="header-anchor" href="#功能介绍">¶</a></h2><h3 id="设备管理" tabindex="-1">设备管理 <a class="header-anchor" href="#设备管理">¶</a></h3><blockquote><p>根据要报送的通道来填写设备信息，终端编码、终端名称、终端类型、定位功能、所在区县四项参数都是必须的，其他参数根据通道要求填写，不再赘述具体的参数含义。</p></blockquote><h3 id="通道报送" tabindex="-1">通道报送 <a class="header-anchor" href="#通道报送">¶</a></h3><blockquote><p>不同的通道对报送的要求不同，有的不需要报送，只需要在支付时上送设备信息即可，有的需要提前进行上报。提前上报所需的参数也不尽相同，需要我们对不同的通道进行配置。</p></blockquote><ul><li>如果通道已经进行上报或其他处理，可以直接编辑包含状态的信息数据，不需要执行上送或注销操作</li><li>报送状态分为：未报送、已报送、已注销、错误，报送或注销失败时状态为错误，可以手动更改状态</li><li>一个通道可能需要上传多种类型的报送数据，如汇付就需要分开上送微信和支付宝的设备信息</li></ul><h2 id="操作截图" tabindex="-1">操作截图 <a class="header-anchor" href="#操作截图">¶</a></h2><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/picx-images-hosting@master/20250505/wechat_2025-05-05_182945_183.2rvcj0ln0b.webp" alt="wechat_2025-05-05_182945_183" loading="lazy"></p><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/picx-images-hosting@master/20250505/wechat_2025-05-05_183031_258.b9443er3w.webp" alt="wechat_2025-05-05_183031_258" loading="lazy"></p><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/picx-images-hosting@master/20250505/wechat_2025-05-05_183155_847.9rjm0wxzum.webp" alt="wechat_2025-05-05_183155_847" loading="lazy"></p>',12)]))}const m=e(c,[["render",l]]);export{p as __pageData,m as default};
