import{_ as d,c as a,o as e,aj as r}from"./chunks/framework.DbINzAG1.js";const p=JSON.parse('{"title":"订单操作","description":"","frontmatter":{},"headers":[],"relativePath":"plus/manual/admin/trade/订单操作.md","filePath":"plus/manual/admin/trade/订单操作.md","lastUpdated":1749639381000}'),h={name:"plus/manual/admin/trade/订单操作.md"};function o(s,t,l,c,n,i){return e(),a("div",null,t[0]||(t[0]=[r('<h1 id="订单操作" tabindex="-1">订单操作 <a class="header-anchor" href="#订单操作">¶</a></h1><div class="tip custom-block"><p class="custom-block-title">说明</p><p>订单类的操作比较简单，所以将所有类型的订单放在一起进行描述，避免过多的分类。</p></div><h2 id="功能介绍" tabindex="-1">功能介绍 <a class="header-anchor" href="#功能介绍">¶</a></h2><h3 id="支付订单" tabindex="-1">支付订单 <a class="header-anchor" href="#支付订单">¶</a></h3><blockquote><p>系统的核心数据，用于记录用户支付的订单信息。同时可以进行</p></blockquote><h4 id="订单状态" tabindex="-1">订单状态 <a class="header-anchor" href="#订单状态">¶</a></h4><blockquote><p>支付订单中除了自身的状态，还额外记录了该订单进行退款和分账的状态</p></blockquote><table tabindex="0"><thead><tr><th>类型</th><th>状态</th></tr></thead><tbody><tr><td>订单状态</td><td>见常量和状态表<a href="/plus/gateway/interface/overview/常量和状态表.html">支付状态</a>相关的描述</td></tr><tr><td>退款状态</td><td>见常量和状态表<a href="/plus/gateway/interface/overview/常量和状态表.html">退款状态</a>相关的描述</td></tr><tr><td>分账状态</td><td>待补充</td></tr></tbody></table><h4 id="操作类型" tabindex="-1">操作类型 <a class="header-anchor" href="#操作类型">¶</a></h4><table tabindex="0"><thead><tr><th>操作</th><th>功能</th></tr></thead><tbody><tr><td>关闭</td><td>支付中和待支付时可以进行的操作，关闭当前的支付订单</td></tr><tr><td>撤销</td><td>支付中和待支付时可以进行的操作，撤销当前的支付订单，如果通道不支持则会走关闭订单逻辑</td></tr><tr><td>同步</td><td>向通道方同步订单状态信息，处理未接到回调或未自动同步导致状态不一致的订单，<strong>注：只能从执行态流转到终态，无法反向流转</strong></td></tr><tr><td>退款</td><td>可以对支付完成且未退款完毕的订单进行退款，如果已经在退款中则需要完成退款中才可以进行操作</td></tr><tr><td>分账</td><td>支付完成且允许分账的订单可以触发分账操作，使用默认的分账组进行处理</td></tr></tbody></table><h3 id="退款订单" tabindex="-1">退款订单 <a class="header-anchor" href="#退款订单">¶</a></h3><blockquote><p>记录进行退款操作的订单，主要的操作注意用来处理退款失败的订单进行关闭或者重试，此处退款订单的状态会影响到关联支付订单的退款状态</p></blockquote><table tabindex="0"><thead><tr><th>操作</th><th>功能</th></tr></thead><tbody><tr><td>同步</td><td>向通道方同步订单状态信息，处理未接到回调或未自动同步导致状态不一致的订单，<strong>注：只能从执行态流转到终态，无法反向流转</strong></td></tr><tr><td>重试</td><td>退款中和退款失败的订单可以进行退款重试，如果通道无法保证幂等性，则不允许仅需要重试</td></tr><tr><td>关闭</td><td>对退款失败的订单可以进行关闭，关闭后支付订单的退款状态也会从退款中更新其他状态，从而可以重新进行退款操作</td></tr></tbody></table><h3 id="转账订单" tabindex="-1">转账订单 <a class="header-anchor" href="#转账订单">¶</a></h3><blockquote><p>待补充</p></blockquote><table tabindex="0"><thead><tr><th>操作</th><th>功能</th></tr></thead><tbody><tr><td></td><td></td></tr><tr><td></td><td></td></tr><tr><td></td><td></td></tr></tbody></table><h3 id="分账订单" tabindex="-1">分账订单 <a class="header-anchor" href="#分账订单">¶</a></h3><blockquote><p>待补充</p></blockquote><table tabindex="0"><thead><tr><th>操作</th><th>功能</th></tr></thead><tbody><tr><td></td><td></td></tr><tr><td></td><td></td></tr><tr><td></td><td></td></tr></tbody></table><h2 id="操作截图" tabindex="-1">操作截图 <a class="header-anchor" href="#操作截图">¶</a></h2><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/picx-images-hosting@master/20250505/wechat_2025-05-05_210237_669.8dx2x16v9k.webp" alt="wechat_2025-05-05_210237_669" loading="lazy"></p><p><img src="https://cdn.jsdelivr.net/gh/xxm1995/picx-images-hosting@master/20250505/wechat_2025-05-05_210248_032.3gom36t3gs.webp" alt="wechat_2025-05-05_210248_032" loading="lazy"></p>',22)]))}const _=d(h,[["render",o]]);export{p as __pageData,_ as default};
