import{_ as d,c as a,a6 as e,o as r}from"./chunks/framework.dnLRFVS3.js";const m=JSON.parse('{"title":"交易记录信息","description":"","frontmatter":{},"headers":[],"relativePath":"single/admin/trade/交易记录信息.md","filePath":"single/admin/trade/交易记录信息.md","lastUpdated":1740996345000}'),h={name:"single/admin/trade/交易记录信息.md"};function s(i,t,n,l,c,o){return r(),a("div",null,t[0]||(t[0]=[e('<h1 id="交易记录信息" tabindex="-1">交易记录信息 <a class="header-anchor" href="#交易记录信息">¶</a></h1><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>包含多种支付操作时产生的记录信息，通常为数据产生后就不会发生改变</p></div><h2 id="回调记录" tabindex="-1">回调记录 <a class="header-anchor" href="#回调记录">¶</a></h2><div class="tip custom-block"><p class="custom-block-title">提示</p><p>此处展示的回调记录，记录的是三方支付网关网关回调给本系统的信息，而不是本系统发送给业务系统的信息。</p></div><h2 id="参数说明" tabindex="-1">参数说明 <a class="header-anchor" href="#参数说明">¶</a></h2><table tabindex="0"><thead><tr><th>参数</th><th>功能</th></tr></thead><tbody><tr><td>本地订单ID</td><td>根据不同的回调类型，来确定关联的是支付订单还是退款订单</td></tr><tr><td>网关订单号</td><td>三方支付网关中的订单号</td></tr><tr><td>支付通道</td><td>接受通知的异步支付通道，见<a href="/single/guides/other/常量和状态表.html#支付通道-paychannelenum">支付通道</a></td></tr><tr><td>回调类型</td><td>是支付回调还是退款回调，见<a href="/single/guides/other/常量和状态表.html#支付系统中常见的操作类型-paymenttypeenum">支付系统中常见的操作类型</a></td></tr><tr><td>通知消息</td><td>回调的具体内容，存储为json格式</td></tr><tr><td>状态</td><td>回调处理的状态，见<a href="/single/guides/other/常量和状态表.html#支付回调处理状态-paycallbackstatusenum">回调处理状态</a></td></tr><tr><td>通知时间</td><td>接收到通知的时间</td></tr></tbody></table><h2 id="交易流水" tabindex="-1">交易流水 <a class="header-anchor" href="#交易流水">¶</a></h2><h2 id="关闭记录-支付" tabindex="-1">关闭记录(支付) <a class="header-anchor" href="#关闭记录-支付">¶</a></h2><div class="tip custom-block"><p class="custom-block-title">提示</p><p>在支付订单操作或通过接口进行关闭或撤销订单时，创建的关闭记录。</p></div><h2 id="参数说明-1" tabindex="-1">参数说明 <a class="header-anchor" href="#参数说明-1">¶</a></h2><table tabindex="0"><thead><tr><th>参数</th><th>功能</th></tr></thead><tbody><tr><td>支付记录id</td><td>支付订单的id</td></tr><tr><td>业务号</td><td>支付订单的业务号</td></tr><tr><td>支付通道</td><td>关闭订单是什么支付通道</td></tr><tr><td>是否关闭成功</td><td>是或者否</td></tr><tr><td>错误消息</td><td>关闭失败时的错误消息</td></tr><tr><td>客户端IP</td><td>发起关闭操作的客户端IP</td></tr><tr><td>关闭时间</td><td>发起关闭操作的时间</td></tr></tbody></table><h2 id="同步记录" tabindex="-1">同步记录 <a class="header-anchor" href="#同步记录">¶</a></h2><div class="tip custom-block"><p class="custom-block-title">提示</p><p>通过触发同步可以实现查询三方支付网关的订单信息，然后与本地订单进行比对，如果不相同，可以根据类型的不同触发修复行为。有些其他的功能也是基于同步来实现， 例如系统中的超时关闭，就是通过定时任务触发同步的，通过同步结束后触发的修复行为来实现的。</p></div><h3 id="参数说明-2" tabindex="-1">参数说明 <a class="header-anchor" href="#参数说明-2">¶</a></h3><table tabindex="0"><thead><tr><th>参数</th><th>功能</th></tr></thead><tbody><tr><td>本地订单号</td><td>根据修复类型的不同，可能是支付ID/退款ID</td></tr><tr><td>本地业务号</td><td>根据修复类型的不同，可能是支付业务号/退款号</td></tr><tr><td>网关订单号</td><td>三方支付网关订单号</td></tr><tr><td>同步类型</td><td>支付同步/退款同步，见<a href="/single/guides/other/常量和状态表.html#支付系统中常见的操作类型-paymenttypeenum">支付系统中常见的操作类型</a></td></tr><tr><td>同步通道</td><td>同步的异步支付通道，见<a href="/single/guides/other/常量和状态表.html#支付通道-paychannelenum">支付通道</a></td></tr><tr><td>同步结果</td><td>见 <a href="/single/guides/other/常量和状态表.html#支付同步状态-paysyncstatusenum">支付同步状态</a> 和 <a href="/single/guides/other/常量和状态表.html#退款同步状态-refundsyncstatusenum">退款同步状态</a></td></tr><tr><td>修复单号</td><td>如果订单状态不一致，触发修复行所生成的修复单号，注：退款同步会分别生成退款同步和支付同步的修复单，通过单号进行串联在一起</td></tr><tr><td>同步时间</td><td>发起同步的时间</td></tr><tr><td>同步消息</td><td>从三方支付网关中获取到订单信息</td></tr><tr><td>错误信息</td><td>同步失败的错误信息</td></tr><tr><td>客户端IP</td><td>发起同步操作的客户端IP</td></tr></tbody></table><h2 id="操作界面" tabindex="-1">操作界面 <a class="header-anchor" href="#操作界面">¶</a></h2>',16)]))}const b=d(h,[["render",s]]);export{m as __pageData,b as default};
